import React, { Component }  from 'react';

import  Service  from  './Service';
import  Comments from './Comments';
import { Link } from 'react-router-dom';



const  service  =  new  Service();

class Article extends Component{

  constructor(props) {
    super(props);

    const { match: { params } } = this.props;
    let article = []
    let fref = 'Article'+params.pk
    if(localStorage.getItem(fref)){
      article = JSON.parse(localStorage.getItem('catalog'))
    }
    this.state = {
      article:  article,
      id: params.pk
    };
  }


  componentDidMount(){
    var  self  =  this;
    let fref = 'Article'+this.state.id
    service.getArticle({id: this.state.id}).then(function (result) {
      if(!localStorage.getItem(fref) || JSON.parse(localStorage.getItem(fref)) !== result){
        self.setState({ catalog: result })
        localStorage.setItem(fref, JSON.stringify(result))
      }
      self.setState({ article: result })
    });
  }

  styleOfArticle(key) {
    if (key[1] === "code") {
      return  (<div key={key} className="article__code">
                {key[0]}
              </div>)
    } else if(key[1] === "text"){
      return (<div key={key} className="article__text">
                {key[0]}
              </div>)
    } else if(key[1] === "link"){
      return (<div key={key} className="article__link">
                <a href={key[2]}> {key[0]}</a>
              </div>)
    } else if(key[1] === "header"){
      return (<div key={key} className="article__header">
                {key[0]}
              </div>)
    }
  }


  componentDidUpdate(prevProps) {
    const { match: { params } } = this.props;
    if (params.pk !== this.state.opend) {
      this.setState({opend: params.pk});
    }
  }

  render() {

    return(
        <div className="article__container">
          { 
            this.state.article.map( moment  =>
              this.styleOfArticle(moment)
            )
          }

          <Link className="article__readact btn btn-success" to={"/ArticleRedactor/"+this.state.opend}>
            Предложить исправление
          </Link>
          <Comments id={this.state.id}/>
      </div>
    );
  }
}

export default Article;
